import{_ as r,c as i,o as a,ag as e}from"./chunks/framework.CEWvfau7.js";const b=JSON.parse('{"title":"Welcome to CHRIST (Deemed to be University) Library","description":"","frontmatter":{},"headers":[],"relativePath":"Library/Library.md","filePath":"Library/Library.md"}'),o={name:"Library/Library.md"};function s(n,t,l,d,c,h){return a(),i("div",null,t[0]||(t[0]=[e('<h1 id="welcome-to-christ-deemed-to-be-university-library" tabindex="-1">Welcome to CHRIST (Deemed to be University) Library <a class="header-anchor" href="#welcome-to-christ-deemed-to-be-university-library" aria-label="Permalink to &quot;Welcome to CHRIST (Deemed to be University) Library&quot;">​</a></h1><p>You have access to 5 specialized libraries:</p><table tabindex="0"><thead><tr><th><strong>Campus</strong></th><th><strong>Library Name</strong></th><th><strong>Specialization</strong></th></tr></thead><tbody><tr><td><strong>Bangalore Central Campus</strong></td><td>Knowledge Centre</td><td>Post Graduate (PG), Law, and Research Library</td></tr><tr><td></td><td>Under Graduate (UG) Library</td><td>B.Ed. and Science Library</td></tr><tr><td><strong>Bangalore Kengeri Campus</strong></td><td>Knowledge Centre</td><td>MBA and Engineering Library</td></tr><tr><td><strong>Bangalore Bannerghatta Road Campus</strong></td><td>Knowledge Centre</td><td>Business Studies and Social Science Library</td></tr><tr><td><strong>Bangalore Yeshwanthpur Campus</strong></td><td>Knowledge Centre</td><td>Business Studies and Social Science Library</td></tr></tbody></table><h2 id="library-facilities-services" tabindex="-1">Library Facilities &amp; Services <a class="header-anchor" href="#library-facilities-services" aria-label="Permalink to &quot;Library Facilities &amp; Services&quot;">​</a></h2><ul><li>Circulation of books</li><li>Reference of Books/Periodicals/Encyclopedia</li><li>Open Access to online Journals/Articles</li><li>Desktops, Internet, and Wi-Fi</li><li>SMS and Email for all Library transactions</li><li>Reprography</li><li>Locker Facility</li><li>Symposium Halls (Discussion Rooms)</li><li>Magazines/Newspapers</li><li>Journal Archive</li><li>Laptop Charging Points</li><li>Orientation Programmes</li></ul><h2 id="library-catalogue" tabindex="-1">Library Catalogue <a class="header-anchor" href="#library-catalogue" aria-label="Permalink to &quot;Library Catalogue&quot;">​</a></h2><p>Our catalogue can be accessed online at <a href="http://library.christuniversity.in" target="_blank" rel="noreferrer">http://library.christuniversity.in</a></p><h3 id="library-catalogue-features" tabindex="-1">Library Catalogue Features: <a class="header-anchor" href="#library-catalogue-features" aria-label="Permalink to &quot;Library Catalogue Features:&quot;">​</a></h3><ul><li>Search for an item (by Author, Title, Subject, ISBN, and Keyword)</li><li>Locate the item</li><li>Get bibliographic details of the item</li><li>Review available/checked-out items</li><li>Determine return dates</li></ul><p>The Library has <strong>Online Public Access Catalogue (OPAC)</strong> terminals at various locations inside the library.</p><h3 id="library-circulation-account-features" tabindex="-1">Library Circulation Account Features: <a class="header-anchor" href="#library-circulation-account-features" aria-label="Permalink to &quot;Library Circulation Account Features:&quot;">​</a></h3><ul><li><strong>Summary</strong> - Overview of borrowed books</li><li><strong>Fines</strong> - Paid and outstanding fines</li><li><strong>Details</strong> - Registration number and contact details</li><li><strong>Search history</strong> - History of your OPAC searches</li><li><strong>Reading history</strong> - Books borrowed during your tenure</li><li><strong>Purchase Suggestions</strong> - Recommend books for purchase</li><li><strong>Messages</strong> - Library notifications</li><li><strong>Lists</strong> - Create a personal reading list</li></ul><br><div class="tip custom-block"><p class="custom-block-title">Information on New Arrivals</p><p>A list of books added in the last 15 days can be viewed in the catalogue.</p><blockquote><p><strong><a href="https://library.christuniversity.in/upcoming/kelibrary.php#" target="_blank" rel="noreferrer">New Arrivals</a></strong></p></blockquote></div><h2 id="books-their-arrangement" tabindex="-1">Books &amp; Their Arrangement <a class="header-anchor" href="#books-their-arrangement" aria-label="Permalink to &quot;Books &amp; Their Arrangement&quot;">​</a></h2><p>The library has a collection including:</p><ul><li>Textbooks</li><li>Reference books</li><li>Encyclopedias and Dictionaries</li><li>Working Papers</li><li>PhD/MPhil Thesis</li><li>Master&#39;s Dissertations</li><li>Bound volumes of Journals</li><li>Braille Books</li></ul><p>Books are arranged according to the <strong>22nd edition of the Dewey Decimal Classification</strong>.</p><h2 id="institutional-membership" tabindex="-1">Institutional Membership - ?? <a class="header-anchor" href="#institutional-membership" aria-label="Permalink to &quot;Institutional Membership - ??&quot;">​</a></h2><h2 id="borrowing-facility" tabindex="-1">Borrowing Facility <a class="header-anchor" href="#borrowing-facility" aria-label="Permalink to &quot;Borrowing Facility&quot;">​</a></h2><ul><li>Books marked <strong>REF</strong> (Reference) cannot be borrowed.</li><li>Books with a <strong>white spine label marked as BK</strong> are available for circulation.</li></ul><h3 id="borrowing-limits-duration" tabindex="-1">Borrowing Limits &amp; Duration <a class="header-anchor" href="#borrowing-limits-duration" aria-label="Permalink to &quot;Borrowing Limits &amp; Duration&quot;">​</a></h3><table tabindex="0"><thead><tr><th>User Category</th><th>No. of Books</th><th>Duration</th></tr></thead><tbody><tr><td>UG Students</td><td>2</td><td>14 days</td></tr><tr><td>Law Students</td><td>2</td><td>7 days</td></tr><tr><td>PG Students</td><td>4</td><td>14 days</td></tr><tr><td>Research Scholars</td><td>10</td><td>21 days</td></tr><tr><td>Faculty</td><td>20</td><td>90 days</td></tr><tr><td>Law Faculty</td><td>20</td><td>30 days</td></tr><tr><td>Staff</td><td>5</td><td>30 days</td></tr></tbody></table><p><strong>Late Fee</strong>: ₹5.00 per day</p><h2 id="library-activities" tabindex="-1">Library Activities <a class="header-anchor" href="#library-activities" aria-label="Permalink to &quot;Library Activities&quot;">​</a></h2><ul><li><strong>Bookmark</strong> - Annual Book Exhibition (July)</li><li><strong>Event-Oriented Book Displays</strong> - Conducted during national and international events</li><li><strong>Information Display</strong> - Notices, new arrivals, and event updates</li></ul><h2 id="ask-the-librarian" tabindex="-1">Ask the Librarian <a class="header-anchor" href="#ask-the-librarian" aria-label="Permalink to &quot;Ask the Librarian&quot;">​</a></h2><p>For research or service queries, contact the librarian in person or via email: <code>library@christuniversity.in</code></p><h2 id="purchase-suggestions" tabindex="-1">Purchase Suggestions <a class="header-anchor" href="#purchase-suggestions" aria-label="Permalink to &quot;Purchase Suggestions&quot;">​</a></h2><p>Faculty and students can request book purchases by emailing the librarian. Students should route requests through their department head.</p><h2 id="expectations-from-library-users" tabindex="-1">Expectations from Library Users <a class="header-anchor" href="#expectations-from-library-users" aria-label="Permalink to &quot;Expectations from Library Users&quot;">​</a></h2><ul><li>Carry your <strong>ID card</strong> for transactions</li><li>Maintain <strong>silence</strong> in the library</li><li>Follow <strong>University dress code</strong></li><li><strong>No cell phones</strong> inside the library</li><li>Do not write or highlight books</li><li>Use dustbins for cleanliness</li></ul><h2 id="library-timings" tabindex="-1">Library Timings <a class="header-anchor" href="#library-timings" aria-label="Permalink to &quot;Library Timings&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Days</th><th>Timings</th></tr></thead><tbody><tr><td>Monday - Friday</td><td>8:00 AM - 9:00 PM</td></tr><tr><td>Sunday</td><td>10:00 AM - 6:00 PM</td></tr><tr><td>Vacation (Mon-Fri)</td><td>8:00 AM - 5:00 PM</td></tr></tbody></table><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p>The library is <strong>closed on public holidays</strong>.</p></div><h2 id="contact-us" tabindex="-1">Contact Us <a class="header-anchor" href="#contact-us" aria-label="Permalink to &quot;Contact Us&quot;">​</a></h2><ul><li><strong>Email</strong>: <a href="mailto:library@christuniversity.in" target="_blank" rel="noreferrer">library@christuniversity.in</a></li><li><strong>Phone Numbers</strong>: <ul><li>Knowledge Centre: <strong>080 4012 9660 / 9661</strong></li><li>UG Library: <strong>080 4012 9114</strong></li><li>Kengeri Campus: <strong>080 4012 9882</strong></li><li>BGR Campus: <strong>080 4655 1443</strong></li><li>YPR Campus: <strong>080 6989 6506</strong></li></ul></li></ul>',37)]))}const g=r(o,[["render",s]]);export{b as __pageData,g as default};
